const baseURL = 'https://jsonplaceholder.typicode.com/posts';
import axios from "axios";

export  const  getPosts = async ()=>{
    let posts = []
    try {
        const response = await axios.get(baseURL)
        posts = response.data;
    } catch (error) {
        console.log(error)
    }
    console.log('after resolve')
    return {
        type:'POSTS_LIST',
        payload:posts 
    }

}
export const  getUsers = async ()=>{
    let users = []
    try {
        const response = await axios.get(baseURL)
        users = response.data;
    } catch (error) {
        console.log(error)
    }
    console.log('after resolve')
    return {
        type:'USERS_LIST',
        payload:users 
    }

}
export const getPostDetails = async (id)=>{
    let post = {}
    try {
        const response = await axios.get(`${baseURL}/${id}`)
       post = response.data;
    } catch (error) {
        console.log(error)
    }
    console.log('after resolve')
    return {
        type:'POSTS_DETAILS',
        payload:post 
    }
}

export const clearDetails = ()=>{
    return {
        type:'CLEAR_DETAILS'
    }
}
